INSERT INTO POST(ID, CONTENT, 	DATE, SUMMARY, TITLE, AUTHOR_ID)VALUES(1, 'Modernizar o passado<br/>É uma evolução musical<br/>Cadê as notas que estavam aqui?<br/>Não presciso delas!<br/>Basta deixar tudo soando bem aos ouvidos<br/>O medo dá origem ao mal<br/>O homem coletivo sente a necessidade de lutar<br/>O orgulho, a arrogância, a glória<br/>Enche a imaginação de domínio<br/>São demônios os que destroem o poder<br/>Bravio da humanidade<br/>Viva Zapata<br/>Viva Sandino<br/>Antônio Conselheiro<br/>Todos os Panteras Negras<br/>Lampião sua imagem e semelhança<br/>Eu tenho certeza eles também cantaram um dia','1994-01-01','Artista: Nação Zumbi<br/>Álbum: Da Lama ao Caos<br/>Data de lançamento: 1994','Monologo ao pé do ouvido',2);
INSERT INTO POST(ID, CONTENT, 	DATE, SUMMARY, TITLE, AUTHOR_ID)VALUES(2, 'Têm para mim Chamados de outro mundo<br/>as Noites perigosas e queimadas,<br/>quando a Lua aparece mais vermelha<br/>São turvos sonhos, Mágoas proibidas,<br/>são Ouropéis antigos e fantasmas<br/>que, nesse Mundo vivo e mais ardente<br/>consumam tudo o que desejo Aqui.<br/><br/>Será que mais Alguém vê e escuta?<br/><br/>Sinto o roçar das asas Amarelas<br/>e escuto essas Canções encantatórias<br/>que tento, em vão, de mim desapossar.<br/><br/>Diluídos na velha Luz da lua,<br/>a Quem dirigem seus terríveis cantos?<br/><br/>Pressinto um murmuroso esvoejar:<br/>passaram-me por cima da cabeça<br/>e, como um Halo escuso, te envolveram.<br/>Eis-te no fogo, como um Fruto ardente,<br/>a ventania me agitando em torno<br/>esse cheiro que sai de teus cabelos.<br/><br/>Que vale a natureza sem teus Olhos,<br/>ó Aquela por quem meu Sangue pulsa?<br/><br/>Da terra sai um cheiro bom de vida<br/>e nossos pés a Ela estão ligados.<br/>Deixa que teu cabelo, solto ao vento,<br/>abrase fundamente as minhas mão...<br/><br/>Mas, não: a luz Escura inda te envolve,<br/>o vento encrespa as Águas dos dois rios<br/>e continua a ronda, o Som do fogo.<br/><br/>Ó meu amor, por que te ligo à Morte?<br/><br/>','1945-01-01','Poema de Ariano Suassuna publicado no Jornal do comercio em 1945','Noturno',1);
INSERT INTO POST(ID, CONTENT, 	DATE, SUMMARY, TITLE, AUTHOR_ID)VALUES(3, 'Subi no muro do quintal<br/>E vi uma transa que não é normal<br/>E ninguém (guem, guem, guem) vai acreditar<br/>Eu vi duas mulheres<br/>Botando aranha prá brigar<br/>Duas aranhas, duas aranhas<br/>Duas aranhas, duas aranhas<br/>Vem cá mulher deixa de manha<br/>Minha cobra quer comer sua aranha<br/>Meu corpo todo se tremeu<br/>E nem a cobra entendeu<br/>Como é que pode duas aranhas se esfregando<br/>Eu tô sabendo, alguma coisa tá faltando<br/>A minha cobra, cobra criada<br/>A minha cobra, cobra criada<br/>Vem cá mulher deixa de manha<br/>Minha cobra quer comer sua aranha<br/>Deve ter uma boa explicação<br/>O que é que essas aranhas tão fazendo ali no chão<br/>Uma em cima, a outra embaixo<br/>E a cobra perguntando onde é que eu me encaixo?<br/>A minha cobra, cobra criada<br/>A minha cobra, cobra criada<br/>Vem cá mulher deixa de manha<br/>Minha cobra qué comer sua aranha<br/>Soltei a cobra e ela foi direto<br/>Foi pro meio das aranhas<br/>Prá mostrar como é que é certo<br/>Cobra com aranha é que dá pé<br/>Aranha com aranha sempre deu em jacaré<br/>É minha cobra, cobra com aranha<br/>É minha cobra, com as aranhas<br/>Vem cá mulher dexa de manha<br/>Minha cobra qué comer sua aranha','1980-01-01', 'Canção do album Abre-te Sésamo de 1980','Rock das aranhas',3);
